//Prototipo de funciones
void pantallaPrincipal();
void pantallaDC();
void pantallaServo();
void pantallaPaso();
void angulosVerde();
void angulosBlack();

int botonesMenu(float x, float y);
int botonesPaso(float x, float y);
int scrollDC(float x);
int tortaServo(float x, float y);

int main()
{

    TFT.claim(stdout); // arranca el stdout de la TFT
    TFT.set_orientation(0);
    TFT.background(Red);   // set background to black
    TFT.foreground(White); // set chars to white
    TFT.cls();             // clear the screen
    //first show the 4 directions
    TFT.set_orientation(2);
    TFT.cls();
    myTouch.InitTouch();
    myTouch.SetPrecision(PREC_LOW);
    pantallaPrincipal();
}

//////Definición de funciones

int botonesMenu(float x, float y)
{
    int a = 0;
    /////////////DC
    if (y > 57 && x > 81 && y < 102 && x < 104)
    {
        return 1;
    }
    /////////////Servo
    if (y > 2 && x > 81 && y < 46 && x < 104)
    {
        return 2;
    }
    /////////////Paso a paso
    if (y > 2 && x > 115 && y < 102 && x < 138)
    {
        return 3;
    }
    return 0;
}
//////////////////////////////////////
void pantallaPrincipal()
{
    bool isRunning = true;

    //Texto
    TFT.set_font((unsigned char *)Arial28x28);
    TFT.fillrect(0, 0, 240, 320, Red);
    TFT.locate(37, 26);
    printf("Universidad");
    TFT.locate(25, 59);
    printf("Distrital F.J.C.");
    TFT.set_font((unsigned char *)Arial24x23);
    TFT.locate(15, 100);
    printf("Brayan Snader");
    TFT.locate(19, 127);
    printf("Galeano Lara");
    TFT.set_font((unsigned char *)Arial12x12);
    TFT.locate(55, 154);
    printf("COD:20192573049");

    //Botones
    TFT.background(Black);
    TFT.set_font((unsigned char *)Arial28x28);
    TFT.fillrect(20, 180, 110, 235, Black);
    TFT.locate(43, 193);
    printf("DC");
    TFT.fillrect(125, 180, 225, 235, Black);
    TFT.locate(132, 193);
    printf("Servo");
    TFT.fillrect(20, 250, 225, 305, Black);
    TFT.locate(27, 263);
    printf("Paso a paso");

    //Decoración
    TFT.rect(5, 5, 235, 315, Black);
    TFT.rect(20, 180, 110, 235, White);
    TFT.rect(125, 180, 225, 235, White);
    TFT.rect(20, 250, 225, 305, White);

    while (isRunning)
    {

        if (myTouch.DataAvailable())
        {
            if (myTouch.Read())
            {
                x = myTouch.GetX(); // toma x del touch
                y = myTouch.GetY(); // toma y del touch

                TFT.locate(110, 60);

                teclaPrecionada = botonesMenu(x, y);

                switch (teclaPrecionada)
                {
                case 1:
                    isRunning = false;
                    pantallaDC();
                    break;
                case 2:
                    isRunning = false;
                    pantallaServo();
                    break;
                case 3:
                    isRunning = false;
                    pantallaPaso();
                    break;
                default:
                    //TFT.printf("Se salio");
                    break;
                }
            }
        }
    }
}